function isEligibleUser(user) {
  return user.age >= 18 && user.active;
}

function formatUser(user) {
  return {
    id: user.id,
    name: user.name.toUpperCase(),
    email: user.email.toLowerCase()
  };
}

function processUserData(users) {
  return users
    .filter(isEligibleUser)
    .map(formatUser);
}

