const DISCOUNT_RATES = {
  premium: { 5: 0.2, 2: 0.15, 0: 0.1 },
  regular: { 3: 0.05, 0: 0 }
};

function getDiscount(user) {
  const rates = DISCOUNT_RATES[user.type];
  if (!rates) return 0;

  const thresholds = Object.keys(rates)
    .map(Number)
    .sort((a, b) => b - a);

  const threshold = thresholds
    .find(t => user.yearsActive >= t);

  return rates[threshold] || 0;
}
